class Session {

    // retourne the token de la session storage
    static getId = () => sessionStorage.getItem('id') || null;

    // retourne la valeur de stay du session storage
    static getStayConnected = () => sessionStorage.getItem('stay');

    // modifie le token et l'utilisateur de la session storage
    static setId = (id) => sessionStorage.setItem('id', id);

    // met à "true" stay dans la session storage
    static setStayConnected = () => sessionStorage.setItem('stay', "true");

    // retire le token et l'utilisateur de la session storage
    static clean = () => sessionStorage.clear();

}


class User {
    constructor() {
        this.users = [
            {
                id: 1, username: "admin", password: "admin", token: "token 1",
                nom: "nom admin", prenom: "prenom admin", pays: "Cameroun", adresse: "adresse admin",
                statut: true, role: "ADMIN"
            },

            {
                id: 2, username: "username 2", password: "password 2", token: "token 2",
                nom: "nom 2", prenom: "prenom 2", pays: "Cameroun", adresse: "adresse 2",
                statut: true, role: "USER"
            },

            {
                id: 3, username: "username 3", password: "password 3", token: "token 3",
                nom: "nom 3", prenom: "prenom 3", pays: "Cameroun", adresse: "adresse 3",
                statut: true, role: "USER"
            },

            {
                id: 4, username: "username 4", password: "password 4", token: "token 4",
                nom: "nom 4", prenom: "prenom 4", pays: "Cameroun", adresse: "adresse 4",
                statut: true, role: "USER"
            },

            {
                id: 5, username: "username 5", password: "password 5", token: "token 5",
                nom: "nom 5", prenom: "prenom 5", pays: "Cameroun", adresse: "adresse 5",
                statut: true, role: "USER"
            },
        ]
    }

    findAll = () => this.users;

    findById = id => this.users[this.users.findIndex(elt => elt.id === id)];

    findByToken = token => this.users[this.users.findIndex(elt => elt.token === token)];

    _findByLogin = (username) => this.users[this.users.findIndex(elt => elt.username === username)];

    login = (username, password, stay) => {
        const userLogged = this._findByLogin(username);
        if (!!userLogged && userLogged.password === password){
            if (stay){
                Session.setStayConnected();
                Session.setId(userLogged.id);
            }
            return {response: true, message: `Bon retour parmi nous ${userLogged.nom}`}
        }
        return {response: false, message: !!userLogged ? "Mot de passe incorrect" : "Nom d'utilisateur ou Email incorrect"}
    };

    logout = (username) => Session.clean();

    passwordReset = email => console.log("Mot de passe changé")
}


export {User, Session}
