import React, {useState, useEffect, useContext} from "react";
import {DataSessionContext, DataUserContext} from "../../utils";

import {MdPerson, MdPersonOutline, MdLockOutline} from 'react-icons/md'
import {Button, Checkbox, Input} from "antd";

import "./Login.css"
import Logo from '../../assets/logo.png'


export default function Login() {
    const manageSession = useContext(DataSessionContext);
    const manageUsers = useContext(DataUserContext);

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [stay, setStay] = useState("on");

    useEffect(() => {
        document.title += " | Connexion"

    }, []);

    const handleChangeEmail = e => {
        setEmail(e.target.value)
        console.log(email)
    }

    const handleChangePassword = e => {
        setPassword(e.target.value)
        console.log(password)
    }

    return (
        <div className="login_wrapper">
            <div className="login_brand">
                <div className="login_brand--left">
                    <a href="https://topinvestici.net/"><img src={Logo} alt="logo"/></a>
                    <div className="login_brand--text">
                        <h2>FuelTracker</h2>
                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Deleniti dolore ducimus eius
                            exercitationem facere nemo reiciendis sint sit suscipit velit.</p>
                    </div>
                </div>
                <form className="login_brand--right">
                    <p className="icon--login"><MdPerson/></p>
                    <Input value={email} onChange={handleChangeEmail} autoComplete="off" size="large" placeholder="Email ou nom d'utlisateur" prefix={<MdPersonOutline />} />
                    <Input value={password} onChange={handleChangePassword} type="password" autoComplete="off" size="large" placeholder="Mot de passe" prefix={<MdLockOutline />} />
                    <Checkbox value={stay} onChange={(e) => setStay(stay + e.target.value)}>Restez connecté ?</Checkbox>
                    <Button type="submit" className="my-secondary-color" size="large">Se connecter</Button>
                    <a href="">Mot de passe oublié ?</a>
                </form>
            </div>
        </div>
    )
}
