import React, {useState, useEffect, useContext} from "react";
import {DataSessionContext, DataUserContext} from "../../utils";

import {MdPerson, MdPersonOutline, MdLockOutline} from 'react-icons/md'
import {Button, Checkbox, Input} from "antd";

import "./Login.css"
import Logo from '../../assets/logo.png'


export default function Login() {
    const manageSession = useContext(DataSessionContext);
    const manageUsers = useContext(DataUserContext);

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [stay, setStay] = useState(false);

    const emailReg  = new RegExp(/^([\w-\.]+)@((?:[\w]+\.)+)([a-zA-Z]{2,4})/i)
    const passwordReg  = new RegExp(/^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])[0-9a-zA-Z]{8,}$/)

    let validEmail = null
    let validAll = false

    useEffect(() => {
        document.title += " | Connexion"
        if (email !== "" && password.length > 7){
            validEmail = emailReg.test(email)
            validAll = true
            console.log(email, validEmail, password, validAll)
        }


    }, [email, password]);

    const handleChangeEmail = e => {
        setEmail(e.target.value)
    }

    const handleChangePassword = e => {
        setPassword(e.target.value)
    }

    return (
        <div className="login_wrapper">
            <div className="login_brand">
                <div className="login_brand--left">
                    <a href="https://topinvestici.net/"><img src={Logo} alt="logo"/></a>
                    <div className="login_brand--text">
                        <h2>FuelTracker</h2>
                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Deleniti dolore ducimus eius
                            exercitationem facere nemo reiciendis sint sit suscipit velit.</p>
                    </div>
                </div>
                <form className="login_brand--right field_error_username">
                    <p className="icon--login"><MdPerson/></p>
                    <Input id="username" value={email} onChange={handleChangeEmail} autoComplete="off" required size="large" placeholder="Email ou nom d'utlisateur" prefix={<MdPersonOutline    />} />
                    <Input id="password" value={password} onChange={handleChangePassword} type="password" required autoComplete="off" size="large" placeholder="Mot de passe" prefix={<MdLockOutline />} />
                    <Checkbox checked={stay} onChange={(e) => setStay(e.target.checked)}>Restez connecté ?</Checkbox>
                    <Button type="submit" className="my-secondary-color" size="large" disabled={!validAll}>Se connecter</Button>
                    <a href="">Mot de passe oublié ?</a>
                </form>
            </div>
        </div>
    )
}
