class Session {

    // retourne the token de la session storage
    static getId = () => sessionStorage.getItem('id') || null;

    // retourne la valeur de stay du session storage
    static getStayConnected = () => sessionStorage.getItem('stay');

    // modifie le token et l'utilisateur de la session storage
    static setId = (id) => sessionStorage.setItem('id', id);

    // met à "true" stay dans la session storage
    static setStayConnected = () => sessionStorage.setItem('stay', "true");

    // retire le token et l'utilisateur de la session storage
    static clean = () => {
        sessionStorage.removeItem(('id'))
        sessionStorage.clear();
    }

}


